          return {
              "x": moment( item["DT_TransactionDate"]).subtract(1, "month").startOf("month").format('MMMM'),
               "y": parseFloat(item.NR_Amount)
          }
      }))
  .groupBy('x')
  .map((x, id) => ({
    x:id,
    y: _.sumBy(x, 'y'),
  }))
  .value()}}

